INSERT INTO `peliculas` (`id`, `titulo`, `duracion`, `genero`, `sinopsis`, `cartel`, `trailer`, `id_youtube`, `fechaEstreno`, `sesion_id`) VALUES
(1, 'La monja', 96, 'Terror/Misterio', 'Cuando una joven monja se quita la vida en un aislado monasterio de clausura en Rumanía.', './monja.jpg', 'https://www.youtube.com/watch?v=eqVjGwYFVqQ', 'eqVjGwYFVqQ', '2024-09-07 00:00:00', 1),
(2, 'Ant-Man i la Avispa: Quantumania', 125, 'Acción/Aventura', 'Scott Lang (Paul Rudd) está disfrutando de su vida como Vengador y de la fama que ha ganado por ayudar a salvar a la humanidad en Vengadores: Endgame. ', './ant.jpg', 'https://www.youtube.com/watch?v=ZlNFpri-Y40', 'ZlNFpri-Y40', '2024-04-17 00:00:00', 2),
(3, 'Night Swim', 98, 'Terror/Misterio', 'Es historia de Ray Waller , una estrella del béisbol obligada a retirarse antes de tiempo debido a una enfermedad que le impide jugar.', './swim.jpg', 'https://www.youtube.com/watch?v=pcSNqteCEtE', 'pcSNqteCEtE', '2024-03-09 00:00:00', 3),
(4, 'Annabelle', 99, 'Terror/Misterio', 'Una pareja comienza a sufrir experiencias terroríficamente supernaturales en su hogar debido a una muñeca vintage.', './anna.jpg', 'https://www.youtube.com/watch?v=8kmLHwKH31M', '8kmLHwKH31M', '2024-10-08 00:00:00', 4),
(5, 'Guardianes de la Galaxia vol. 3', 149, 'Acción/Aventura', 'Puede que Thanos , así como el padre de Peter Quill , Ego, sean ya historia, pero la horrible huella que han dejado tras de sí aún se palpa en el seno de los Guardianes de la Galaxia. ', './guardianes.jpg', 'https://www.youtube.com/watch?v=F60FZKt_36s', 'F60FZKt_36s', '2024-05-05 00:00:00', NULL),
(6, 'Torrente, el brazo tonto de la ley', 97, 'Comedia/Acción', 'José Luis Torrente es un incompetente policía madrileño al que el destino va a dar la oportunidad de redimirse.', './torrente.jpg', 'https://www.youtube.com/watch?v=ulJSg4GYbBE', 'ulJSg4GYbBE', '2024-04-13 00:00:00', NULL),
(7, 'Resacon en las Vegas', 100, 'Comedia/Misterio', 'Después de una noche de excesos, los chicos comienzan el día sin recordar nada de lo que hicieron la noche anterior', './resaca.jpg', 'https://www.youtube.com/watch?v=MckEHmSMg-0', 'MckEHmSMg-0', '2024-08-14 00:00:00', NULL),
(8, 'Avengers: Infinity War', 149, 'Acción/Ciencia ficción', 'Un nuevo peligro acecha procedente de las sombras del cosmos. Thanos, el infame tirano intergaláctico, tiene como objetivo reunir las seis Gemas del Infinito.', './infinty.jpg', 'https://www.youtube.com/watch?v=6ZfuNTqbHE8', '6ZfuNTqbHE8', '2024-04-27 00:00:00', NULL),
(9, 'Jurassic World', 124, 'Acción/Ciencia ficción', ' Isla Nubla tiene ahora un parque temático de dinosaurios en pleno funcionamiento llamado Jurassic World, como lo había previsto John Hammond.', './jurasic.jpg', 'https://www.youtube.com/watch?v=hv9eSCijf6E', 'hv9eSCijf6E', '2024-06-10 00:00:00', NULL),
(10, 'Dune', 155, 'Ciencia ficción/Aventura', 'Dune sigue la historia de Paul Atreides, un joven brillante y talentoso nacido en un futuro lejano en el planeta Arrakis, que es conocido como Dune.', './dune.jpg', 'https://www.youtube.com/watch?v=TTgk_iT8Uts', 'TTgk_iT8Uts', '2024-10-22 00:00:00', NULL),
(11, 'Spider-Man: No Way Home', 148, 'Acción/Aventura', 'Spider-Man: No Way Home sigue la historia de Peter Parker, quien busca la ayuda del Doctor Strange para alterar la realidad después de que su identidad como Spider-Man es revelada.', './spiderman.jpg', 'https://www.youtube.com/watch?v=SkmRT3M4Vx4', 'SkmRT3M4Vx4', '2024-12-17 00:00:00', NULL),
(12, 'Shang-Chi y la leyenda de los Diez Anillos', 132, 'Acción/Aventura', 'Shang-Chi y la leyenda de los Diez Anillos sigue la historia de Shang-Chi, quien debe enfrentarse a su pasado cuando es atraído por la organización terrorista de su padre, los Diez Anillos.', './shangchi.jpg', 'https://www.youtube.com/watch?v=MQRF_YezCgA', 'MQRF_YezCgA', '2024-09-03 00:00:00', NULL),
(13, 'Shin-chan: Aventuras en Henderland', 97, 'Infantil/Aventures', 'Henderland es el parque de atracciones más popular de Kasukabe, y Shin-chan consigue asistir como invitado.', './shin.jpg', 'https://www.youtube.com/watch?v=7E2tfw4j6q4', '7E2tfw4j6q4', '2024-04-01 00:00:00', NULL),
(14, 'Wall-e', 98, 'Infantil/CF', '700 años atrás, el planeta Tierra tuvo que ser evacuado debido a una enorme acumulación de basura, encomendando a una serie de robots la misión de limpiarla.', './wall.jpg', 'https://www.youtube.com/watch?v=qF7p4lZ00RA', 'qF7p4lZ00RA', '2024-04-17 00:00:00', NULL),
(15, 'Doraemon, el gladiador', 81, 'Infantil/Aventures', 'En esta nueva aventura de Doraemon y Nobita conoceremos a Poko, un niño robot que llega desde el futuro con una de las máquinas del tiempo del gato cósmico.', './doraemon.jpg', 'https://www.youtube.com/watch?v=MwA6yK-gjSU', 'MwA6yK-gjSU', '2024-03-31 00:00:00', NULL),
(16, 'Cars', 117, 'Infantil/Comèdia', 'Un pequeño y nuevo coche de carreras, llamado Rayo McQueen, se está preparando para ser un experto corredor y convertirse en uno de los automóviles más respetados en el panorama internacional.', './cars.jpg', 'https://www.youtube.com/watch?v=8ZMklX5BzTo', '8ZMklX5BzTo', '2024-04-22 00:00:00', NULL);
